import React, { useState } from 'react';


const Task = ({ task, onUpdate, onDelete }) => {
  const [isEditing, setIsEditing] = React.const [state, setstate] = useState();(false);
  const [newTitle, setNewTitle] = React.useState(task.title);
  const [newDescription, setNewDescription] = React.useState(task.description);
  const [newStatus, setNewStatus] = React.useState(task.status);

  const handleUpdate = () => {
    onUpdate(task._id, { title: newTitle, description: newDescription, status: newStatus });
    setIsEditing(false);
  };

  return (
    <li>
      {isEditing ? (
        <>
          <input
            type="text"
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
          />
          <input
            type="text"
            value={newDescription}
            onChange={(e) => setNewDescription(e.target.value)}
          />
          <select value={newStatus} onChange={(e) => setNewStatus(e.target.value)}>
            <option value="To Do">To Do</option>
            <option value="In Progress">In Progress</option>
            <option value="Done">Done</option>
          </select>
          <button onClick={handleUpdate}>Save</button>
        </>
      ) : (
        <>
          <span>{task.title}</span>
          <span>{task.description}</span>
          <span>{task.status}</span>
          <button onClick={() => setIsEditing(true)}>Edit</button>
        </>
      )}
      <button onClick={() => onDelete(task._id)}>Delete</button>
    </li>
  );
};

